using osu.Framework.Graphics.Primitives;

namespace osu.Framework.Text
{
    /// <summary>
    /// Describes a character generated by <see cref="TextBuilder"/>.
    /// </summary>
    public struct CharacterInfo
    {
        /// <summary>
        /// The glyph which this character represents.
        /// </summary>
        public CharacterGlyph Glyph;

        /// <summary>
        /// The rectangle for the character to be drawn in.
        /// </summary>
        public RectangleF DrawRectangle;

        /// <summary>
        /// Whether this is the first character on a new line.
        /// </summary>
        public bool OnNewLine;
    }
}
